select.if:
SELECT TBL_ID AS tblId /* 테이블ID */ , ITM_NM AS itmNm /* 칼럼명 */ , CLMN_ID AS itmId /* 칼럼ID */ , DATA_TP_CD AS dataTpCd /* 데이터타입ID */ , PK_YN AS pkYn /* PK여부 */ , VLVL_VRF_YN AS vlvlVrfYn /* 유효값검증여부 */ , VLVL_CD AS vlvlCd /* 유효값코드 */ , ORD_SEQ AS ordSeq /* 정렬순서 */ , ITM_DSC AS itmDsc /* 항목설명 */ , SEL_BAS_YN AS selBasYn /* 조회기준여부 */ FROM TB_UPL_ITM_LST WHERE 1=1
AND CS_NO = #{csNo}
AND TBL_ID = #{tblIdTxt} /* 테이블ID로 필터링 */ ORDER BY TBL_ID, ORD_SEQ
